    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        String name = "", email = "", gender = "", course = "", round = "";
        if (txtName.getText().trim().length() < 4) {
            JOptionPane.showMessageDialog(null, "Enter Your Name");
        } else if (!emailValid(txtEmail.getText()) || txtEmail.getText().trim().length() < 1) {
            JOptionPane.showMessageDialog(null, "Enter Your valid Email");
        } else {
            System.out.println("Name :" + txtName.getText());
            System.out.println("Email : " + txtEmail.getText());
        }
    }//GEN-LAST:event_btnAddActionPerformed
    public static boolean emailValid(String email) {
        int atpos = email.indexOf("@");
        int dotpos = email.indexOf(".");
        if (atpos > 1 && (dotpos - atpos) > 2 && dotpos < email.length() - 2) {
            return true;
        }
        return false;
    }

